import React from "react";
import Counter from "./counter";

export default class ContainerTwo extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            counts: {}
        };
    }

    countClicks(name) {
        this.setState(state => {
            state.counts[name]
                ? state.counts[name] += 1
                : state.counts[name] = 1;
            return state;
        });
    }

    render() {
        return (
            <>
            <Counter 
            name="One" 
            count={this.state.counts.one}
            onClick={this.countClicks}
            />
            <Counter 
            name="Two" 
            count={this.state.counts.two}
            onClick={this.countClicks}
            />
            <Counter 
            name="Three" 
            count={this.state.counts.one}
            onClick={this.countClicks}
            />
            </>
        );
    }
}
