import React from "react";
import Counter from "./counter";

export default class ContainerTwo extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            counts: {},
            highestCount: 0
        };
        this.countClicks = this.countClicks.bind(this);
    }

    countClicks(name) {
        this.setState(state => {
            state.counts[name]
                ? state.counts[name] += 1
                : state.counts[name] = 1;
            return state;
        });
    }

    render() {
        return (
            <>
            <Counter 
            name="one" 
            count={this.state.counts.one}
            action={this.countClicks}
            />
            <Counter 
            name="two" 
            count={this.state.counts.two}
            action={this.countClicks}
            />
            <Counter 
            name="three" 
            count={this.state.counts.three}
            action={this.countClicks}
            />
            </>
        );
    }
}
